CREATE TABLE [dbo].[PROVIDER_PRACTICE_STRUCTURE] (
    [PROVIDER_PRACTICE_STRUCTURE_ID]          INT           IDENTITY (1, 1) NOT NULL,
    [PDTS_PROVIDER_ID]                        INT           NOT NULL,
    [EMPLOYMENT_AGREEMENT_TYPE]               BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_EMPLOYMENT_AGREEMENT_TYPE] DEFAULT ((0)) NOT NULL,
    [INCOME_GUARANTEE_AGREEMENT_TYPE]         BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_INCOME_GUARANTEE_AGREEMENT_TYPE] DEFAULT ((0)) NOT NULL,
    [SOLO_STRUCTURE]                          BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_SOLO] DEFAULT ((0)) NOT NULL,
    [GROUP_STRUCTURE]                         BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_GROUP] DEFAULT ((0)) NOT NULL,
    [NUMBER_OF_PHYSICIANS_IN_GROUP]           INT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_NUMBER_OF_PHYSICIANS_IN_GROUP] DEFAULT ((0)) NOT NULL,
    [SINGLE_SPECIALTY]                        BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_SINGLE_SPECIALTY] DEFAULT ((0)) NOT NULL,
    [MULTI_SPECIALTY]                         BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_MULTI_SPECIALTY] DEFAULT ((0)) NOT NULL,
    [MULTI_SPECIALTIES]                       VARCHAR (200) NULL,
    [MIDLEVEL_PROVIDERS_IN_GROUP]             BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_MIDLEVEL_PROVIDERS_IN_GROUP] DEFAULT ((0)) NOT NULL,
    [CALL_REQUIREMENTS]                       VARCHAR (50)  NULL,
    [NUMBER_OF_OTHER_PHYSICIANS_IN_SPECIALTY] INT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_NUMBER_OF_OTHER_PHYSICIANS_IN_SPECIALTY] DEFAULT ((0)) NOT NULL,
    [RECRUIT_FOR_EXISTING_PRACTICE]           BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_RECRUIT_FOR_EXISTING_PRACTICE] DEFAULT ((0)) NOT NULL,
    [RECRUIT_FOR_NEW_PRACTICE]                BIT           CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_RECRUIT_FOR_NEW_PRACTICE] DEFAULT ((0)) NOT NULL,
    [PROVIDER_PRACTICE_STRUCTURE_SPACE_ID]    INT           NULL,
    [CREATE_DATE]                             DATETIME      CONSTRAINT [DF_PROVIDER_PRACTICE_STRUCTURE_CREATE_DATE] DEFAULT (getdate()) NOT NULL,
    [CREATED_BY_USER_ID]                      INT           NOT NULL,
    [UPDATE_DATE]                             DATETIME      NULL,
    [UPDATED_BY_USER_ID]                      INT           NULL,
    [OTHER_SPACE]                             VARCHAR (100) NULL,
    CONSTRAINT [PK_PROVIDER_PRACTICE_STRUCTURE] PRIMARY KEY CLUSTERED ([PROVIDER_PRACTICE_STRUCTURE_ID] ASC),
    CONSTRAINT [FK_PROVIDER_PRACTICE_STRUCTURE_CreateUser] FOREIGN KEY ([CREATED_BY_USER_ID]) REFERENCES [dbo].[PdtsUser] ([USER_ID]),
    CONSTRAINT [FK_PROVIDER_PRACTICE_STRUCTURE_Provider] FOREIGN KEY ([PDTS_PROVIDER_ID]) REFERENCES [dbo].[Provider] ([PDTS_PROVIDER_ID]) ON DELETE CASCADE,
    CONSTRAINT [FK_PROVIDER_PRACTICE_STRUCTURE_PROVIDER_PRACTICE_STRUCTURE_SPACE] FOREIGN KEY ([PROVIDER_PRACTICE_STRUCTURE_SPACE_ID]) REFERENCES [dbo].[PROVIDER_PRACTICE_STRUCTURE_SPACE] ([PROVIDER_PRACTICE_STRUCTURE_SPACE_ID]),
    CONSTRAINT [FK_PROVIDER_PRACTICE_STRUCTURE_UpdateUser] FOREIGN KEY ([UPDATED_BY_USER_ID]) REFERENCES [dbo].[PdtsUser] ([USER_ID])
);

